import React, { Component } from 'react';
import Page from '../../components/page';
import axios from 'axios'

export default class Homepage extends Component {

  constructor(props) {
    super(props);
    this.state = {
      head: '',
      description: ''
    }
  }

  componentDidMount() {
    axios.get('http://localhost:4000/homepage').then(res => {
      this.setState({
        head: res.data[0].head,
        description: res.data[0].description
      })
    })
  }

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();
    // if (this.props.match.params.id) {
    //   axios
    //     .post(
    //       "https://server.dotaznik.hardart.cz/admin/user/update/" +this.props.match.params.id,
    //       this.state
    //     )
    //     .then(res => {
    //       window.location.href = "/list/all/" + this.state.typeUser;
    //     });
    // } else {
      axios
        .post("http://localhost:4000/homepage/create", this.state)
        .then(res => {
          window.location.href = "/";
        });
    // }
  };

  render() {
    return (
      <Page id="homepage">
      <div className="uk-container uk-container-xsmall">
        <div className="uk-grid uk-child-width-1-1" uk-grid="">
          <form className="uk-form-stacked">
            <div className="uk-margin">
              <label className="uk-form-label" htmlFor="form-stacked-text">
                Zahlavi
              </label>
              <div className="uk-form-controls">
                <input
                  className="uk-input"
                  value={this.state.head}
                  id="form-stacked-text"
                  name="head"
                  onChange={this.handleChange}
                  type="text"
                  placeholder="Zahlavi"
                />
              </div>
            </div>

            <div className="uk-margin">
              <label className="uk-form-label" htmlFor="form-stacked-text">
                Popis
              </label>
              <textarea
                className="uk-textarea"
                rows="5"
                onChange={this.handleChange}
                name="description"
                placeholder="Popis"
                value={this.state.description}
              />
            </div>

            <button
              className="uk-button uk-button-primary"
              onClick={this.onSubmit}
            >
              Ulozit
            </button>
          </form>
        </div>
      </div>
      </Page>
    )
  }
}
